INSERT INTO `singer` VALUES (1, '周杰伦', 1, '/img/singerPic/1636122801922584_20140806110326513.jpg', '1979-01-08 17:29:15', '中国台湾', '周杰伦（Jay Chou），1979年1月18日出生于台湾省新北市，中国台湾流行乐男歌手，音乐人，演员，导演，编剧，监制，商人。');
INSERT INTO `singer` VALUES (2, '张杰', 1, '/img/singerPic/1636122836283zhangjie.jpg', '1982-12-20 18:50:07', '中国四川', '张杰（Jason Zhang），1982年12月20日出生于四川省成都市，中国流行男歌手。004年出道至今，已发行6张高品质唱片，唱片销量称冠内地群雄, 举办过4场爆满的个人演唱会， 2008年以来在各大权威音乐奖项中先后13次获得“最受欢迎男歌手”称号，2010年在韩国最权威的颁奖礼——韩国M-net亚洲音乐大赏(MAMA,M-net Asia Music Awards)上获得“亚洲之星”（Best Asian Artist）大奖，影响力触及全亚洲，是华语歌坛最闪耀的男歌手之一。');
INSERT INTO `singer` VALUES (3, '林俊杰', 1, '/img/singerPic/1636122903668ljj.jpg', '1981-03-27 00:00:00', '新加坡', '林俊杰(JJ Lin)，1981年3月27日出生于新加坡，华语流行乐坛男歌手、词曲创作者、音乐制作人。');
INSERT INTO `singer` VALUES (4, '朴树', 1, '/img/singerPic/1636122939819pushu.jpg', '1973-11-08 22:07:08', '中国江苏', '朴树，本名濮树，1973年11月8日出生于江苏省南京市，籍贯北京市，中国内地男歌手、音乐制作人、影视演员。');
INSERT INTO `singer` VALUES (5, 'MONACA', 2, '/img/singerPic/T001R300x300M000000Elz6I2yg1PY.jpg', '2004-05-08 12:34:57', '日本', 'MONACA是一个主要进行音乐制作的艺术家群体。在2001年岡部啟一从NAMCO（现在是BANDAI NAMCO GAMES）退出之后，作为自由人继续活动，于2004年10月个人出资300万日元建立了MONACA。');
INSERT INTO `singer` VALUES (6, '陈奕迅', 1, '/img/singerPic/1636123074363chenyixun.jpg', '1974-07-27 00:00:00', '中国香港', '陈奕迅（Eason Chan），1974年7月27日出生于中国香港，祖籍广东省东莞市，中国香港流行乐男歌手、演员、作曲人，毕业于英国金斯顿大学。 1995年参加第14届新秀歌唱大赛并获得冠军，同年正式出道。');
INSERT INTO `singer` VALUES (7, '王力宏', 1, '/img/singerPic/1636123177406wanglihong.jpg', '1976-05-17 14:00:30', '美国', '王力宏（Wang Leehom），1976年5月17日出生于美国纽约罗切斯特，祖籍中国浙江义乌，华语流行乐男歌手、音乐人、影视演员、导演，拥有威廉姆斯大学和伯克利音乐学院双荣誉博士的学历。 ');
INSERT INTO `singer` VALUES (8, '五月天', 2, '/img/singerPic/16361234275842021-11-05 222906.jpg', '1997-03-29 00:00:00', '中国台湾', '五月天（Mayday），中国台湾摇滚乐团，成立于1997年3月29日，由温尚翊（怪兽）、陈信宏（阿信）、石锦航（石头）、蔡升晏（玛莎）、刘谚明（冠佑）五名成员组成。 1998年加入滚石唱片，正式开始音乐历程。');
INSERT INTO `singer` VALUES (9, 'C418', 1, '/img/singerPic/1635767824841Daniel_Rosenfeld_2018.jpg', '1989-05-09 00:00:00', '德国', '丹尼尔·罗森菲尔德，艺名C418（发音为“see four eighteen”），德国音乐家，制作人和音响工程师。以为电子游戏《我的世界》作曲家和设计音效而知名，也为《怪奇物语》的拍摄花絮《Beyond Stranger Things》创作了主题曲。2021年9月1日在Steam上发布的Cookie Clicker版本也包括了C418创作的配乐。');
INSERT INTO `singer` VALUES (10, 'OneRepublic', 2, '/img/singerPic/1636123465861onerepublic.jpg', '2002-07-01 00:00:00', '美国', 'OneRepublic是一支美国流行摇滚乐队，于2002年在科罗拉多州科罗拉多斯普林斯成立，成员包括主唱兼多乐器演奏家瑞恩·泰德、吉他手扎克·菲尔金斯和德鲁·布朗、贝斯手兼大提琴手布伦特·库兹尔、鼓手埃迪·费舍尔和键盘手布赖恩·威利特。 ');
INSERT INTO `singer` VALUES (11, 'Eminem', 1, '/img/singerPic/Eminem.jpg', '1972-10-17 15:35:23', '美国', '埃米纳姆（Eminem）是美国的一名说唱歌手。其风格类型为：Hardcore Rap（硬核说唱）。埃米纳姆最大的突破就是证明白人也能介入到黑人一统天下的说唱（RAP）界中，而且获得巨大的成功。同时他的叛逆不仅长期以来深受美国青少年喜爱，也让他在舆论中始终遭到抨击。Eminem获得的奖杯总数窜至历史第三位，居麦当娜和皮特-加布里埃尔之后。');
INSERT INTO `singer` VALUES (14, '许嵩', 1, '/img/singerPic/xusong.jpg', '1986-05-14 21:58:45', '中国安徽', '著名作词人、作曲人、唱片制作人、歌手。内地独立音乐之标杆人物，有音乐鬼才之称。2009年独立出版首张词曲全创作专辑《自定义》，2010年独立出版第二张词曲全创作专辑《寻雾启示》，两度掀起讨论热潮，一跃成为内地互联网讨论度最高的独立音乐人。2011年加盟海蝶音乐，推出第三张词曲全创作专辑《苏格拉没有底》，发行首月即在大陆地区摘下唱片销量榜冠军，轰动全国媒体，并拉开全国巡回签售及歌迷见面会。');
INSERT INTO `singer` VALUES (15, '张国荣', 1, '/img/singerPic/zhangguorong.jpg', '1956-09-12 22:02:38', '中国香港', '张国荣是一位在全球华人社会和亚洲地区具有影响力的著名歌手、演员和音乐人；大中华区乐坛和影坛巨星；演艺圈多栖发展最成功的代表之一。张国荣是香港乐坛的殿堂级歌手之一，曾获得香港乐坛最高荣誉金针奖；他是第一位享誉韩国乐坛的华人歌手，亦是华语唱片在韩国销量纪录保持者。他通晓词曲创作，曾担任过MTV导演、唱片监制、电影编剧、电影监制。他于1991年当选香港电影金像奖影帝。。。');
INSERT INTO `singer` VALUES (16, '杨宗纬', 1, '/img/singerPic/yangzongwei.jpg', '1978-04-04 22:04:47', '中国台湾', '大学时期参加台湾歌唱选秀节目《超级星光大道》获选为第一届“人气王”。比赛的时候，杨宗纬歌声阳刚而细腻，富含感情，辨识度高，感染力强，以演唱抒情歌曲见长，选曲跨越性别界线，无论是男女歌手的抒情歌曲，经过他重新诠释之后，常常都可以得到不输原唱或甚至超越原唱的评价。出道后屡创多项记录，包括发行首张专辑，便以新人之姿登上台北小巨蛋举办个人演唱会。。。');
INSERT INTO `singer` VALUES (18, '李克勤', 1, '/img/singerPic/likeqin.jpg', '1967-12-06 22:10:04', '中国香港', '中国香港很有影响力的粤语流行曲歌手与演员。1985年凭《雾之恋》夺得“十九区业余歌唱大赛”冠军而晋身乐坛。曾于2002、2003和2005年度《十大劲歌金曲颁奖典礼》中三度夺得「最受欢迎男歌手」，于2003年度《叱吒乐坛流行榜颁奖典礼》上获得「叱吒乐坛我最喜爱的男歌手」，并于《第二十七届十大中文金曲颁奖典礼》(2004年度)上夺得「最优秀流行男歌手大奖」，2010年度音乐先锋榜颁奖礼 ── 20家电台联颁亚太歌手奖。');
INSERT INTO `singer` VALUES (19, '张碧晨', 0, '/img/singerPic/zhangbichen.jpg', '1989-09-10 22:15:16', '中国天津', '1989年9月10日出生于中国天津，中国女歌手。 2013年，张碧晨以韩国女子组合“Sunny days ”出道，并获得“K-POP ”世界庆典“最优秀奖”。2014年7月，张碧晨参加第三季《中国好声音》，以一首《她说》进入那英组，并于10月7日以一首《时间去哪儿了》荣获第三季《中国好声音》全国总冠军，成为《中国好声音》首位女冠军。');
INSERT INTO `singer` VALUES (23, '毛不易', 1, '/img/singerPic/maobuyi.jpg', '1994-10-01 18:59:43', '中国黑龙江', '原名王维家，1994年10月1日出生于黑龙江省齐齐哈尔市泰来县，中国内地流行乐男歌手，毕业于杭州师范大学护理专业。');
INSERT INTO `singer` VALUES (24, '胡歌', 1, '/img/singerPic/huge.jpg', '1982-09-02 17:52:02', '中国上海', '中国著名男演员、歌手，有“古装王子”的美称。2005年毕业于上海戏剧学院01级表演系本科，在电视连续剧《仙剑奇侠传》中成功塑造了豪爽深情的“李逍遥”而成名，他还为此剧唱插曲《六月的雨》《逍遥叹》等。胡歌主演过多部广为人知的影视剧，多部电视剧打破电视台收视记录。其人擅长摄影，文采飞扬，志做导演，频唱影视剧歌曲。2013年，主演的话剧《如梦之梦》和《永远的尹雪艳》登上舞台。');
INSERT INTO `singer` VALUES (31, 'Beyond', 2, '/img/singerPic/1586078551461Beyond.jpg', '1983-01-01 00:00:00', '中国香港', '中国香港摇滚乐队，由黄家驹、黄贯中、黄家强、叶世荣组成。');
INSERT INTO `singer` VALUES (54, 'Claude Debussy', 1, '/img/singerPic/hhh.jpg', '1862-08-22 00:00:00', '法国', '德彪西是19世纪末20世纪初最有影响力的作曲家之一，代表作品有管弦乐《大海》和《牧神午后前奏曲》，钢琴组曲《贝加马斯克组曲》、《意象集》、《版画集》等；而创作最高峰则是歌剧《佩利亚斯与梅丽桑德》。');
INSERT INTO `singer` VALUES (55, 'ODESZA', 2, '/img/singerPic/1635742385718T001R300x300M000000W2YCd0c1FcG.jpg', '2012-09-01 00:00:00', '美国', 'Odesza是一个来自美国华盛顿州西雅图的电子乐团体。乐队由哈里森·米尔斯（Harrison Mills）和克莱顿·奈特（Clayton Knight）在2012年组建。两人是西华盛顿大学的同学。在电子游戏《极限竞速 地平线3》中，他们的《Say My Name》被收录进游戏中的虚拟电台“Horizon Pulse”。');
INSERT INTO `singer` VALUES (57, 'Anna Yvette', 0, '/img/singerPic/1635761593617DaZM8WcC_200x200.jpg', '1990-09-21 00:00:00', '美国', 'Anna Yvette，是一位美国歌手兼音乐制作人。她为\r\nMonstercat的各种歌曲提供人声，包括Break\r\nThem，Against The Sun，Restless，Neon\r\nRainbow等。她还与劳拉·布雷姆（Laura Brehm）\r\n发行了单曲《Summer Never Ends》。在2018年，\r\n与Pegboard Nerds合作的歌曲Party Freaks出现在\r\n《Rocket League X Monstercat Vol.2》专辑中。');
INSERT INTO `singer` VALUES (58, 'CHVRCHES', 2, '/img/singerPic/1635766949334194757164_335516957940079_7563010578163947133_n.jpg', '2011-03-02 00:00:00', '英国', 'Chvrches（风格化写作CHVRCHΞS，有译作“圣堂乐队”）是一支成立于2011年来自英国苏格兰格拉斯哥的流行电音乐队。乐队由三人组成：劳伦·梅拜瑞（主唱、鼓、合成、采样）、伊恩·库克（合成、电吉他、贝斯、和声）以及马丁·多赫蒂（合成、采样、和声）');
INSERT INTO `singer` VALUES (59, '王菲', 0, '/img/singerPic/1636123342380wangfei.jpg', '1969-08-08 17:58:31', '中国北京', '中国著名女歌手、演员。是继邓丽君后大中华地区成就最高、影响力最大的华语女歌手。以其极具辨识度的天籁空灵般嗓音，在华语歌坛创造了属于她自己的时代。她是首位登上美国《时代周刊》封面并接受CNN专访的华语歌手。她是身价最高，演唱会上座率最高，演唱会票房累计最高的华语女歌手。');
INSERT INTO `singer` VALUES (85, 'G.E.M.邓紫棋', 0, '/img/singerPic/1636123006647dengziqi.jpg', '1974-07-27 00:00:00', '中国上海', '邓紫棋（G.E.M.），本名邓诗颖，1991年8月16日出生于上海市，中国香港流行乐女歌手、词曲作者、音乐制作人。');
